ALTER TABLE `zt_news` CHANGE `title` `title` VARCHAR(50) CHARSET utf8 COLLATE utf8_general_ci DEFAULT '' NOT NULL COMMENT '标题', CHANGE `keyword` `keyword` VARCHAR(50) CHARSET utf8 COLLATE utf8_general_ci DEFAULT '' NOT NULL COMMENT '关键字', CHANGE `description` `description` VARCHAR(200) CHARSET utf8 COLLATE utf8_general_ci DEFAULT '' NOT NULL COMMENT '描述', CHANGE `price` `price` INT(11) DEFAULT 0 NOT NULL COMMENT '价格（单位：分）', CHANGE `img` `img` VARCHAR(100) CHARSET utf8 COLLATE utf8_general_ci DEFAULT '' NOT NULL COMMENT '主图', CHANGE `category_id` `category_id` INT(11) DEFAULT 0 NOT NULL COMMENT '分类id', ADD COLUMN `province_id` INT(11) DEFAULT 0 NOT NULL COMMENT '省份id' AFTER `neworold`, ADD COLUMN `city_id` INT(11) DEFAULT 0 NOT NULL COMMENT '城市id' AFTER `province_id`, ADD COLUMN `school_id` INT(11) DEFAULT 0 NOT NULL COMMENT '学校id' AFTER `city_id`, CHANGE `user_id` `user_id` INT(11) DEFAULT 0 NOT NULL COMMENT '用户id', CHANGE `show_count` `show_count` INT(11) DEFAULT 0 NOT NULL COMMENT '浏览次数', CHANGE `is_top` `is_top` INT(1) DEFAULT 0 NOT NULL COMMENT '置顶', CHANGE `report_count` `report_count` INT(11) DEFAULT 0 NOT NULL COMMENT '举报次数', CHANGE `is_del` `is_del` INT(1) DEFAULT 0 NOT NULL COMMENT '删除', CHANGE `created` `created` INT(11) DEFAULT 0 NOT NULL COMMENT '创建时间', CHANGE `updated` `updated` INT(11) DEFAULT 0 NOT NULL COMMENT '更改时间';
ALTER TABLE `zt_university_all` CHANGE `province` `province_id` INT(11) DEFAULT 0 NOT NULL COMMENT '省份id', CHANGE `city` `city_id` INT(11) DEFAULT 0 NOT NULL COMMENT '城市id', CHANGE `s_name` `s_name` VARCHAR(50) CHARSET utf8 COLLATE utf8_general_ci DEFAULT '' NOT NULL COMMENT '学校名称', ADD COLUMN `status` TINYINT DEFAULT 0 NOT NULL COMMENT '状态： 0 正常 1 开通 -1 关闭' AFTER `s_name`; 
ALTER TABLE `zt_user` ADD COLUMN `province_id` INT(11) DEFAULT 0 NOT NULL COMMENT '省id' AFTER `nickname`, ADD COLUMN `city_id` INT(11) DEFAULT 0 NOT NULL COMMENT '市id' AFTER `province_id`, CHANGE `university_id` `school_id` INT(11) DEFAULT 0 NOT NULL COMMENT '学校id'; 