CREATE TABLE `zt_article`( `id` INT NOT NULL AUTO_INCREMENT, `title` VARCHAR(50) NOT NULL DEFAULT '' COMMENT '标题', `content` LONGTEXT NOT NULL COMMENT '内容', `sort` TINYINT NOT NULL DEFAULT 0 COMMENT '排序', `status` TINYINT NOT NULL DEFAULT 0 COMMENT '状态', `query` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '标识字段', `last_handle_user` INT NOT NULL DEFAULT 0 COMMENT '最后修改的管理员id', `created` INT NOT NULL DEFAULT 0 COMMENT '创建时间', `updated` INT NOT NULL DEFAULT 0 COMMENT '修改时间', PRIMARY KEY (`id`) ) ENGINE=INNODB CHARSET=utf8 COLLATE=utf8_general_ci; 