CREATE TABLE `zt_send_mail_log`( `id` INT NOT NULL AUTO_INCREMENT, `subject` VARCHAR(200) NOT NULL DEFAULT '' COMMENT '主题', `body` LONGTEXT NOT NULL COMMENT '正文', `from` VARCHAR(50) NOT NULL DEFAULT '' COMMENT '发送人账号', `from_name` VARCHAR(50) NOT NULL DEFAULT '' COMMENT '发送人名称', `to` VARCHAR(50) NOT NULL DEFAULT '' COMMENT '接收人账号', `status` TINYINT NOT NULL DEFAULT 0 COMMENT '发送状态 0 建立 1发送成功 -1 发送失败', `falg` ENUM('login','reset_password','custom') NOT NULL COMMENT '标识', `message` VARCHAR(100) NOT NULL DEFAULT '' COMMENT '备注 标识为自定义时使用', `send_time` INT NOT NULL DEFAULT 0 COMMENT '发送时间', `created` INT NOT NULL DEFAULT 0 COMMENT '创建时间', `updated` INT NOT NULL DEFAULT 0 COMMENT '更新时间', PRIMARY KEY (`id`) ) ENGINE=INNODB CHARSET=utf8 COLLATE=utf8_general_ci; 